// frontend/src/components/DonationMarketplace.js
import React, { useState, useEffect } from 'react';
import { Container, Typography, Card, CardContent, CardMedia, Grid, Button } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DonationMarketplace = () => {
  const [donations, setDonations] = useState([]);
  const navigate = useNavigate();
  
  useEffect(() => {
    const fetchDonations = async () => {
      try {
        const response = await axios.get('/api/donations');
        setDonations(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchDonations();
  }, []);
  
  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>Donation Marketplace</Typography>
      <Grid container spacing={2}>
        {donations.map(donation => (
          <Grid item xs={12} sm={6} md={4} key={donation.id}>
            <Card>
              {donation.images.length > 0 && (
                <CardMedia
                  component="img"
                  height="140"
                  image={donation.images[0]} // show first image
                  alt={donation.title}
                />
              )}
              <CardContent>
                <Typography gutterBottom variant="h5">{donation.title}</Typography>
                <Typography variant="body2" color="text.secondary">
                  {donation.description}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Donator: {donation.donator.name}
                </Typography>
                <Button variant="outlined" onClick={() => navigate(`/donation/${donation.id}`)}>
                  View Details
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default DonationMarketplace;
